import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Box, 
  TextField, 
  Button, 
  Paper, 
  Typography,
  CircularProgress,
  Alert,
  Snackbar
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import axios from 'axios';

function App() {
  const [message, setMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [serverStatus, setServerStatus] = useState('checking');

  useEffect(() => {
    // Test server connection on component mount
    checkServerConnection();
  }, []);

  const checkServerConnection = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/test');
      setServerStatus('connected');
    } catch (error) {
      setServerStatus('disconnected');
      setError('Unable to connect to the server. Please make sure the server is running.');
    }
  };

  const handleSendMessage = async () => {
    if (!message.trim()) return;

    const userMessage = message.trim();
    setMessage('');
    setLoading(true);
    setError(null);

    // Add user message to chat history
    setChatHistory(prev => [...prev, { sender: 'user', text: userMessage }]);

    try {
      const response = await axios.post('http://localhost:5000/api/chat', {
        message: userMessage
      });

      // Add AI response to chat history
      setChatHistory(prev => [...prev, { sender: 'alexa', text: response.data.response }]);
    } catch (error) {
      console.error('Error:', error);
      const errorMessage = error.response?.data?.error || 'Failed to send message. Please try again.';
      setError(errorMessage);
      setChatHistory(prev => [...prev, { 
        sender: 'alexa', 
        text: 'Sorry, I encountered an error. Please try again.' 
      }]);
    } finally {
      setLoading(false);
    }
  };

  const handleCloseError = () => {
    setError(null);
  };

  return (
    <Container maxWidth="md" sx={{ height: '100vh', py: 4 }}>
      <Paper elevation={3} sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
        <Box sx={{ p: 2, borderBottom: 1, borderColor: 'divider' }}>
          <Typography variant="h5" component="h1" align="center">
            Alexa Chatbot
          </Typography>
          <Typography 
            variant="body2" 
            align="center" 
            color={serverStatus === 'connected' ? 'success.main' : 'error.main'}
          >
            Server Status: {serverStatus === 'connected' ? 'Connected' : 'Disconnected'}
          </Typography>
        </Box>
        
        <Box sx={{ flexGrow: 1, overflow: 'auto', p: 2 }}>
          {chatHistory.map((chat, index) => (
            <Box
              key={index}
              sx={{
                display: 'flex',
                justifyContent: chat.sender === 'user' ? 'flex-end' : 'flex-start',
                mb: 2
              }}
            >
              <Paper
                elevation={1}
                sx={{
                  p: 2,
                  maxWidth: '70%',
                  bgcolor: chat.sender === 'user' ? '#e3f2fd' : '#f5f5f5'
                }}
              >
                <Typography>{chat.text}</Typography>
              </Paper>
            </Box>
          ))}
          {loading && (
            <Box sx={{ display: 'flex', justifyContent: 'flex-start', mb: 2 }}>
              <CircularProgress size={20} />
            </Box>
          )}
        </Box>

        <Box sx={{ p: 2, borderTop: 1, borderColor: 'divider' }}>
          <Box sx={{ display: 'flex', gap: 1 }}>
            <TextField
              fullWidth
              variant="outlined"
              placeholder="Type your message..."
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
              disabled={loading || serverStatus !== 'connected'}
            />
            <Button
              variant="contained"
              color="primary"
              onClick={handleSendMessage}
              disabled={loading || !message.trim() || serverStatus !== 'connected'}
              endIcon={<SendIcon />}
            >
              Send
            </Button>
          </Box>
        </Box>
      </Paper>

      <Snackbar 
        open={!!error} 
        autoHideDuration={6000} 
        onClose={handleCloseError}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert onClose={handleCloseError} severity="error" sx={{ width: '100%' }}>
          {error}
        </Alert>
      </Snackbar>
    </Container>
  );
}

export default App;
