import React, { useState } from 'react';
import styles from './SearchInterface.module.css';
import SearchIcon from '@mui/icons-material/Search';
import AppsIcon from '@mui/icons-material/Apps';
import PsychologyIcon from '@mui/icons-material/Psychology';
import UploadIcon from '@mui/icons-material/Upload';
import SettingsIcon from '@mui/icons-material/Settings';
import SendIcon from '@mui/icons-material/Send';

const SearchInterface = () => {
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearch = (type) => {
    // Handle different types of searches
    console.log(`Performing ${type} search with query:`, searchQuery);
  };

  return (
    <div className={styles.container}>
      <div className={styles.content}>
        {/* Header */}
        <h1 className={styles.title}>
          Ask Fluxa AI <span className={styles.highlight}>Anything</span>
        </h1>

        {/* Navigation */}
        <nav className={styles.nav}>
          <button 
            onClick={() => handleSearch('web')}
            className={styles.navButton}
          >
            <SearchIcon />
            Web Search
          </button>
          <button 
            onClick={() => handleSearch('app')}
            className={styles.navButton}
          >
            <AppsIcon />
            App Builder
          </button>
          <button 
            onClick={() => handleSearch('research')}
            className={styles.navButton}
          >
            <PsychologyIcon />
            Deep Research
          </button>
          <button 
            onClick={() => handleSearch('upload')}
            className={styles.navButton}
          >
            <UploadIcon />
            Upload
          </button>
        </nav>

        {/* Search Input */}
        <div className={styles.searchContainer}>
          <div className={styles.searchInput}>
            <input
              type="text"
              placeholder="Ask anything..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className={styles.input}
            />
            <button className={styles.settingsButton}>
              <SettingsIcon />
            </button>
          </div>
          <button
            onClick={() => handleSearch('deep')}
            className={styles.searchButton}
          >
            Deep Research
            <SendIcon />
          </button>
        </div>
      </div>
    </div>
  );
};

export default SearchInterface;
