import React from 'react';
import {
  Box,
  Typography,
  Switch,
  FormGroup,
  FormControlLabel,
  Divider,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
} from '@mui/material';

const Settings = () => {
  const [theme, setTheme] = React.useState('dark');
  const [notifications, setNotifications] = React.useState(true);
  const [autoSave, setAutoSave] = React.useState(true);

  return (
    <Box sx={{ p: 3, height: '100vh', backgroundColor: '#0F172A' }}>
      <Typography variant="h4" sx={{ color: '#F8FAFC', mb: 3 }}>
        Settings
      </Typography>
      
      <Box sx={{ backgroundColor: '#1E293B', p: 3, borderRadius: 2 }}>
        <FormGroup>
          <FormControl fullWidth sx={{ mb: 3 }}>
            <InputLabel sx={{ color: '#94A3B8' }}>Theme</InputLabel>
            <Select
              value={theme}
              onChange={(e) => setTheme(e.target.value)}
              sx={{
                color: '#F8FAFC',
                '& .MuiOutlinedInput-notchedOutline': {
                  borderColor: '#334155',
                },
                '&:hover .MuiOutlinedInput-notchedOutline': {
                  borderColor: '#475569',
                },
                '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                  borderColor: '#60A5FA',
                },
              }}
            >
              <MenuItem value="light">Light</MenuItem>
              <MenuItem value="dark">Dark</MenuItem>
              <MenuItem value="system">System</MenuItem>
            </Select>
          </FormControl>

          <Divider sx={{ my: 2, borderColor: '#334155' }} />

          <FormControlLabel
            control={
              <Switch
                checked={notifications}
                onChange={(e) => setNotifications(e.target.checked)}
                sx={{
                  '& .MuiSwitch-switchBase.Mui-checked': {
                    color: '#60A5FA',
                  },
                  '& .MuiSwitch-switchBase.Mui-checked + .MuiSwitch-track': {
                    backgroundColor: '#60A5FA',
                  },
                }}
              />
            }
            label="Enable Notifications"
            sx={{ color: '#F8FAFC', mb: 2 }}
          />

          <FormControlLabel
            control={
              <Switch
                checked={autoSave}
                onChange={(e) => setAutoSave(e.target.checked)}
                sx={{
                  '& .MuiSwitch-switchBase.Mui-checked': {
                    color: '#60A5FA',
                  },
                  '& .MuiSwitch-switchBase.Mui-checked + .MuiSwitch-track': {
                    backgroundColor: '#60A5FA',
                  },
                }}
              />
            }
            label="Auto-save Responses"
            sx={{ color: '#F8FAFC' }}
          />
        </FormGroup>
      </Box>
    </Box>
  );
};

export default Settings;
