import React, { useState, useEffect, useMemo, useRef } from 'react';
import { 
  Container, 
  Box, 
  TextField, 
  Button, 
  Paper, 
  Typography,
  CircularProgress,
  Alert,
  Snackbar,
  Avatar,
  AppBar,
  Toolbar,
  IconButton,
  useTheme,
  ThemeProvider,
  createTheme,
  Switch,
  FormControlLabel,
  Badge
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import PersonIcon from '@mui/icons-material/Person';
import Brightness4Icon from '@mui/icons-material/Brightness4';
import Brightness7Icon from '@mui/icons-material/Brightness7';
import AttachFileIcon from '@mui/icons-material/AttachFile';
import ImageIcon from '@mui/icons-material/Image';
import axios from 'axios';

function App() {
  const [message, setMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [serverStatus, setServerStatus] = useState('checking');
  const [mode, setMode] = useState('light');
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const fileInputRef = useRef(null);

  // Create theme based on mode
  const theme = useMemo(
    () =>
      createTheme({
        palette: {
          mode,
          primary: {
            main: mode === 'light' ? '#2196f3' : '#90caf9',
          },
          secondary: {
            main: mode === 'light' ? '#f50057' : '#f48fb1',
          },
          background: {
            default: mode === 'light' ? '#f5f5f5' : '#121212',
            paper: mode === 'light' ? '#ffffff' : '#1e1e1e',
          },
        },
        typography: {
          fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
        },
        components: {
          MuiPaper: {
            styleOverrides: {
              root: {
                borderRadius: '12px',
              },
            },
          },
          MuiAppBar: {
            styleOverrides: {
              root: {
                backgroundColor: mode === 'light' ? '#ffffff' : '#1e1e1e',
                color: mode === 'light' ? 'rgba(0, 0, 0, 0.87)' : '#ffffff',
              },
            },
          },
        },
      }),
    [mode],
  );

  const toggleColorMode = () => {
    setMode((prevMode) => (prevMode === 'light' ? 'dark' : 'light'));
  };

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewUrl(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleUpload = async () => {
    if (!selectedFile) return;

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await axios.post('http://localhost:5000/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      return response.data.url;
    } catch (error) {
      console.error('Upload error:', error);
      setError('Failed to upload file. Please try again.');
      return null;
    }
  };

  const handleSendMessage = async () => {
    if (!message.trim() && !selectedFile) return;

    const userMessage = message.trim();
    setMessage('');
    setLoading(true);
    setError(null);

    let imageUrl = null;
    if (selectedFile) {
      imageUrl = await handleUpload();
      setSelectedFile(null);
      setPreviewUrl(null);
    }

    setChatHistory(prev => [...prev, { 
      sender: 'user', 
      text: userMessage,
      imageUrl: previewUrl
    }]);

    try {
      const response = await axios.post('http://localhost:5000/api/chat', {
        message: userMessage,
        imageUrl: imageUrl
      });

      setChatHistory(prev => [...prev, { sender: 'alexa', text: response.data.response }]);
    } catch (error) {
      console.error('Error:', error);
      const errorMessage = error.response?.data?.error || 'Failed to send message. Please try again.';
      setError(errorMessage);
      setChatHistory(prev => [...prev, { 
        sender: 'alexa', 
        text: 'Sorry, I encountered an error. Please try again.' 
      }]);
    } finally {
      setLoading(false);
    }
  };

  const handleCloseError = () => {
    setError(null);
  };

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ 
        minHeight: '100vh',
        bgcolor: 'background.default',
        display: 'flex',
        flexDirection: 'column'
      }}>
        <AppBar position="static" elevation={0}>
          <Toolbar>
            <SmartToyIcon sx={{ mr: 2, color: 'primary.main' }} />
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              Alexa Chatbot
            </Typography>
            <Box sx={{ 
              display: 'flex', 
              alignItems: 'center',
              gap: 2
            }}>
              <Box sx={{ 
                display: 'flex', 
                alignItems: 'center',
                bgcolor: serverStatus === 'connected' ? 'success.light' : 'error.light',
                px: 2,
                py: 0.5,
                borderRadius: 1
              }}>
                <Typography variant="caption" sx={{ color: 'white' }}>
                  {serverStatus === 'connected' ? 'Connected' : 'Disconnected'}
                </Typography>
              </Box>
              <IconButton onClick={toggleColorMode} color="inherit">
                {mode === 'dark' ? <Brightness7Icon /> : <Brightness4Icon />}
              </IconButton>
            </Box>
          </Toolbar>
        </AppBar>

        <Container maxWidth="md" sx={{ flexGrow: 1, py: 3 }}>
          <Paper elevation={2} sx={{ 
            height: 'calc(100vh - 200px)',
            display: 'flex',
            flexDirection: 'column',
            overflow: 'hidden'
          }}>
            <Box sx={{ 
              flexGrow: 1, 
              overflow: 'auto', 
              p: 2,
              display: 'flex',
              flexDirection: 'column',
              gap: 2
            }}>
              {chatHistory.map((chat, index) => (
                <Box
                  key={index}
                  sx={{
                    display: 'flex',
                    alignItems: 'flex-start',
                    gap: 1,
                    flexDirection: chat.sender === 'user' ? 'row-reverse' : 'row'
                  }}
                >
                  <Avatar sx={{ 
                    bgcolor: chat.sender === 'user' ? 'primary.main' : 'secondary.main',
                    width: 32,
                    height: 32
                  }}>
                    {chat.sender === 'user' ? <PersonIcon /> : <SmartToyIcon />}
                  </Avatar>
                  <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
                    {chat.imageUrl && (
                      <Paper
                        elevation={0}
                        sx={{
                          p: 1,
                          maxWidth: '300px',
                          bgcolor: 'transparent'
                        }}
                      >
                        <img 
                          src={chat.imageUrl} 
                          alt="Uploaded content" 
                          style={{ 
                            maxWidth: '100%', 
                            borderRadius: '8px',
                            maxHeight: '200px',
                            objectFit: 'contain'
                          }} 
                        />
                      </Paper>
                    )}
                    <Paper
                      elevation={0}
                      sx={{
                        p: 2,
                        maxWidth: '70%',
                        bgcolor: chat.sender === 'user' 
                          ? (mode === 'light' ? 'primary.light' : 'primary.dark')
                          : (mode === 'light' ? 'grey.100' : 'grey.800'),
                        borderRadius: 2,
                        position: 'relative',
                        '&::before': {
                          content: '""',
                          position: 'absolute',
                          top: '12px',
                          [chat.sender === 'user' ? 'right' : 'left']: '-8px',
                          width: 0,
                          height: 0,
                          borderStyle: 'solid',
                          borderWidth: '8px 8px 0 8px',
                          borderColor: `${chat.sender === 'user' 
                            ? (mode === 'light' ? 'primary.light' : 'primary.dark')
                            : (mode === 'light' ? 'grey.100' : 'grey.800')} transparent transparent transparent`,
                          transform: chat.sender === 'user' ? 'rotate(90deg)' : 'rotate(-90deg)'
                        }
                      }}
                    >
                      <Typography variant="body1" sx={{ wordBreak: 'break-word' }}>
                        {chat.text}
                      </Typography>
                    </Paper>
                  </Box>
                </Box>
              ))}
              {loading && (
                <Box sx={{ 
                  display: 'flex', 
                  alignItems: 'center',
                  gap: 1,
                  ml: 1
                }}>
                  <Avatar sx={{ 
                    bgcolor: 'secondary.main',
                    width: 32,
                    height: 32
                  }}>
                    <SmartToyIcon />
                  </Avatar>
                  <CircularProgress size={20} />
                </Box>
              )}
            </Box>

            <Box sx={{ 
              p: 2, 
              borderTop: 1, 
              borderColor: 'divider',
              bgcolor: 'background.paper'
            }}>
              <Box sx={{ 
                display: 'flex', 
                gap: 1,
                alignItems: 'center'
              }}>
                <input
                  type="file"
                  ref={fileInputRef}
                  style={{ display: 'none' }}
                  onChange={handleFileSelect}
                  accept="image/*"
                />
                <IconButton
                  onClick={() => fileInputRef.current?.click()}
                  color="primary"
                  disabled={loading || serverStatus !== 'connected'}
                >
                  <Badge 
                    color="secondary" 
                    variant="dot" 
                    invisible={!selectedFile}
                  >
                    <AttachFileIcon />
                  </Badge>
                </IconButton>
                <TextField
                  fullWidth
                  variant="outlined"
                  placeholder="Type your message..."
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                  disabled={loading || serverStatus !== 'connected'}
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      borderRadius: 2,
                    }
                  }}
                />
                <Button
                  variant="contained"
                  color="primary"
                  onClick={handleSendMessage}
                  disabled={loading || (!message.trim() && !selectedFile) || serverStatus !== 'connected'}
                  sx={{
                    minWidth: '48px',
                    height: '48px',
                    borderRadius: '50%',
                    p: 0
                  }}
                >
                  <SendIcon />
                </Button>
              </Box>
              {previewUrl && (
                <Box sx={{ mt: 1, display: 'flex', alignItems: 'center', gap: 1 }}>
                  <img 
                    src={previewUrl} 
                    alt="Preview" 
                    style={{ 
                      maxWidth: '100px', 
                      maxHeight: '100px',
                      borderRadius: '8px',
                      objectFit: 'cover'
                    }} 
                  />
                  <Button
                    size="small"
                    color="error"
                    onClick={() => {
                      setSelectedFile(null);
                      setPreviewUrl(null);
                    }}
                  >
                    Remove
                  </Button>
                </Box>
              )}
            </Box>
          </Paper>
        </Container>

        <Snackbar 
          open={!!error} 
          autoHideDuration={6000} 
          onClose={handleCloseError}
          anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
        >
          <Alert 
            onClose={handleCloseError} 
            severity="error" 
            sx={{ 
              width: '100%',
              borderRadius: 2
            }}
          >
            {error}
          </Alert>
        </Snackbar>
      </Box>
    </ThemeProvider>
  );
}

export default App;
