import React, { useState, useRef } from 'react';
import {
  Box,
  TextField,
  Button,
  Paper,
  Typography,
  CircularProgress,
  Alert,
  Snackbar,
  AppBar,
  Toolbar,
  IconButton,
  Avatar,
  Menu,
  MenuItem,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Divider,
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import PersonIcon from '@mui/icons-material/Person';
import AttachFileIcon from '@mui/icons-material/AttachFile';
import DeleteIcon from '@mui/icons-material/Delete';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import LogoutIcon from '@mui/icons-material/Logout';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const ChatApp = () => {
  const [message, setMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [anchorEl, setAnchorEl] = useState(null);
  const [profileOpen, setProfileOpen] = useState(false);
  const [clearChatOpen, setClearChatOpen] = useState(false);
  const fileInputRef = useRef(null);
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleMenuClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const handleProfileOpen = () => {
    setProfileOpen(true);
    handleMenuClose();
  };

  const handleProfileClose = () => {
    setProfileOpen(false);
  };

  const handleClearChatOpen = () => {
    setClearChatOpen(true);
    handleMenuClose();
  };

  const handleClearChatClose = () => {
    setClearChatOpen(false);
  };

  const handleClearChat = () => {
    setChatHistory([]);
    handleClearChatClose();
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewUrl(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSendMessage = async () => {
    if (!message.trim() && !selectedFile) return;

    const userMessage = message.trim();
    setMessage('');
    setLoading(true);
    setError(null);

    let imageUrl = null;
    if (selectedFile) {
      const formData = new FormData();
      formData.append('file', selectedFile);
      try {
        const response = await axios.post('http://localhost:5000/api/upload', formData);
        imageUrl = response.data.url;
      } catch (error) {
        setError('Failed to upload image');
        setLoading(false);
        return;
      }
      setSelectedFile(null);
      setPreviewUrl(null);
    }

    setChatHistory(prev => [...prev, { 
      sender: 'user', 
      text: userMessage || (imageUrl ? 'Analyze this image' : ''),
      imageUrl: previewUrl
    }]);

    try {
      const response = await axios.post('http://localhost:5000/api/chat', {
        message: userMessage,
        imageUrl: imageUrl
      });

      setChatHistory(prev => [...prev, { 
        sender: 'alexa', 
        text: response.data.response 
      }]);
    } catch (error) {
      setError('Failed to send message');
    } finally {
      setLoading(false);
    }
  };

  const handleCloseError = () => {
    setError(null);
  };

  return (
    <Box sx={{ display: 'flex', height: '100vh', overflow: 'hidden' }}>
      <AppBar position="fixed">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Alexa Chat
          </Typography>
          <IconButton
            color="inherit"
            onClick={handleMenuClick}
          >
            <Avatar sx={{ bgcolor: 'primary.main' }}>
              {user?.name?.[0] || <PersonIcon />}
            </Avatar>
          </IconButton>
          <Menu
            anchorEl={anchorEl}
            open={Boolean(anchorEl)}
            onClose={handleMenuClose}
          >
            <MenuItem onClick={handleProfileOpen}>
              <ListItemIcon>
                <AccountCircleIcon fontSize="small" />
              </ListItemIcon>
              <ListItemText>Profile</ListItemText>
            </MenuItem>
            <MenuItem onClick={handleClearChatOpen}>
              <ListItemIcon>
                <DeleteIcon fontSize="small" />
              </ListItemIcon>
              <ListItemText>Clear Chat</ListItemText>
            </MenuItem>
            <Divider />
            <MenuItem onClick={handleLogout}>
              <ListItemIcon>
                <LogoutIcon fontSize="small" />
              </ListItemIcon>
              <ListItemText>Logout</ListItemText>
            </MenuItem>
          </Menu>
        </Toolbar>
      </AppBar>

      {/* Profile Dialog */}
      <Dialog open={profileOpen} onClose={handleProfileClose}>
        <DialogTitle>Profile Information</DialogTitle>
        <DialogContent>
          <List>
            <ListItem>
              <ListItemText
                primary="Name"
                secondary={user?.name || 'Not set'}
              />
            </ListItem>
            <ListItem>
              <ListItemText
                primary="Email"
                secondary={user?.email || 'Not set'}
              />
            </ListItem>
          </List>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleProfileClose}>Close</Button>
        </DialogActions>
      </Dialog>

      {/* Clear Chat Dialog */}
      <Dialog open={clearChatOpen} onClose={handleClearChatClose}>
        <DialogTitle>Clear Chat History</DialogTitle>
        <DialogContent>
          <Typography>
            Are you sure you want to clear all chat history? This action cannot be undone.
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClearChatClose}>Cancel</Button>
          <Button onClick={handleClearChat} color="error">
            Clear
          </Button>
        </DialogActions>
      </Dialog>

      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          width: '100%',
          height: '100%',
          display: 'flex',
          flexDirection: 'column',
          pt: '64px',
        }}
      >
        <Paper
          elevation={0}
          sx={{
            flexGrow: 1,
            display: 'flex',
            flexDirection: 'column',
            overflow: 'hidden',
            borderRadius: 2,
            bgcolor: 'background.paper',
          }}
        >
          <Box
            sx={{
              flexGrow: 1,
              overflowY: 'auto',
              p: 2,
              display: 'flex',
              flexDirection: 'column',
              gap: 2,
            }}
          >
            {chatHistory.map((chat, index) => (
              <Box
                key={index}
                sx={{
                  display: 'flex',
                  justifyContent: chat.sender === 'user' ? 'flex-end' : 'flex-start',
                  gap: 1,
                }}
              >
                <Box
                  sx={{
                    maxWidth: '70%',
                    p: 2,
                    borderRadius: 2,
                    bgcolor: chat.sender === 'user' ? 'primary.main' : 'background.paper',
                    color: chat.sender === 'user' ? 'white' : 'text.primary',
                    boxShadow: 1,
                  }}
                >
                  {chat.text}
                  {chat.imageUrl && (
                    <Box sx={{ mt: 1 }}>
                      <img
                        src={chat.imageUrl}
                        alt="Uploaded"
                        style={{ maxWidth: '100%', borderRadius: 8 }}
                      />
                    </Box>
                  )}
                </Box>
              </Box>
            ))}
            {loading && (
              <Box sx={{ display: 'flex', justifyContent: 'flex-start' }}>
                <CircularProgress size={24} />
              </Box>
            )}
          </Box>

          <Box
            sx={{
              p: 2,
              borderTop: 1,
              borderColor: 'divider',
              bgcolor: 'background.paper',
            }}
          >
            <Box sx={{ display: 'flex', gap: 1 }}>
              <TextField
                fullWidth
                variant="outlined"
                placeholder="Type your message..."
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                sx={{
                  '& .MuiOutlinedInput-root': {
                    borderRadius: 2,
                  },
                }}
              />
              <input
                type="file"
                ref={fileInputRef}
                style={{ display: 'none' }}
                onChange={handleFileSelect}
                accept="image/*"
              />
              <IconButton
                color="primary"
                onClick={() => fileInputRef.current?.click()}
              >
                <AttachFileIcon />
              </IconButton>
              <Button
                variant="contained"
                color="primary"
                onClick={handleSendMessage}
                disabled={loading || (!message && !selectedFile)}
                sx={{ borderRadius: 2 }}
              >
                <SendIcon />
              </Button>
            </Box>
          </Box>
        </Paper>
      </Box>

      <Snackbar
        open={error !== null}
        autoHideDuration={6000}
        onClose={handleCloseError}
      >
        <Alert onClose={handleCloseError} severity="error" sx={{ width: '100%' }}>
          {error}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default ChatApp;
