import React, { useState } from 'react';

const SearchInterface = () => {
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearch = (type) => {
    // Handle different types of searches
    console.log(`Performing ${type} search with query:`, searchQuery);
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-8">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <h1 className="text-4xl font-bold mb-8">
          Ask Fluxa AI <span className="text-cyan-400">Anything</span>
        </h1>

        {/* Navigation */}
        <div className="flex gap-6 mb-8">
          <button 
            onClick={() => handleSearch('web')}
            className="flex items-center gap-2 text-gray-300 hover:text-white"
          >
            <span className="material-icons">search</span>
            Web Search
          </button>
          <button 
            onClick={() => handleSearch('app')}
            className="flex items-center gap-2 text-gray-300 hover:text-white"
          >
            <span className="material-icons">apps</span>
            App Builder
          </button>
          <button 
            onClick={() => handleSearch('research')}
            className="flex items-center gap-2 text-gray-300 hover:text-white"
          >
            <span className="material-icons">psychology</span>
            Deep Research
          </button>
          <button 
            onClick={() => handleSearch('upload')}
            className="flex items-center gap-2 text-gray-300 hover:text-white"
          >
            <span className="material-icons">upload</span>
            Upload
          </button>
        </div>

        {/* Search Input */}
        <div className="flex gap-4">
          <div className="flex-1 relative">
            <input
              type="text"
              placeholder="Ask anything..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full bg-gray-800 rounded-lg px-4 py-3 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-cyan-400"
            />
            <button className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400">
              <span className="material-icons">settings</span>
            </button>
          </div>
          <button
            onClick={() => handleSearch('deep')}
            className="px-6 py-3 bg-cyan-500 hover:bg-cyan-600 rounded-lg flex items-center gap-2"
          >
            Deep Research
            <span className="material-icons">send</span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default SearchInterface;
