import React from 'react';
import { Box, List, ListItem, ListItemIcon, ListItemText, styled } from '@mui/material';
import { Link, useLocation } from 'react-router-dom';
import PlayCircleOutlineIcon from '@mui/icons-material/PlayCircleOutline';
import HistoryIcon from '@mui/icons-material/History';
import StarBorderIcon from '@mui/icons-material/StarBorder';
import SettingsIcon from '@mui/icons-material/Settings';

const StyledListItem = styled(ListItem)(({ theme, active }) => ({
  borderRadius: '8px',
  margin: '4px 0',
  color: active ? '#60A5FA' : '#64748B',
  backgroundColor: active ? 'rgba(96, 165, 250, 0.1)' : 'transparent',
  '&:hover': {
    backgroundColor: 'rgba(96, 165, 250, 0.1)',
    color: '#60A5FA',
  },
}));

const Sidebar = () => {
  const location = useLocation();

  const menuItems = [
    { text: 'Playground', icon: <PlayCircleOutlineIcon />, path: '/playground' },
    { text: 'History', icon: <HistoryIcon />, path: '/history' },
    { text: 'Starred', icon: <StarBorderIcon />, path: '/starred' },
    { text: 'Settings', icon: <SettingsIcon />, path: '/settings' },
  ];

  return (
    <Box
      sx={{
        width: 240,
        height: '100vh',
        borderRight: '1px solid',
        borderColor: 'divider',
        p: 2,
        backgroundColor: '#1A1F2E',
      }}
    >
      <List>
        {menuItems.map((item) => (
          <StyledListItem
            key={item.text}
            component={Link}
            to={item.path}
            active={location.pathname === item.path ? 1 : 0}
          >
            <ListItemIcon sx={{ color: 'inherit', minWidth: 40 }}>
              {item.icon}
            </ListItemIcon>
            <ListItemText 
              primary={item.text}
              sx={{
                '& .MuiListItemText-primary': {
                  fontWeight: location.pathname === item.path ? 600 : 400,
                }
              }}
            />
          </StyledListItem>
        ))}
      </List>
    </Box>
  );
};

export default Sidebar;
