import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from './SearchInterface.module.css';
import SearchIcon from '@mui/icons-material/Search';
import AppsIcon from '@mui/icons-material/Apps';
import PsychologyIcon from '@mui/icons-material/Psychology';
import UploadIcon from '@mui/icons-material/Upload';
import SettingsIcon from '@mui/icons-material/Settings';
import SendIcon from '@mui/icons-material/Send';

const SearchInterface = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const fileInputRef = React.useRef(null);

  const handleSearch = async (type) => {
    if (!searchQuery.trim() && type !== 'upload') {
      setError('Please enter a search query');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      switch (type) {
        case 'web':
          // Web search functionality
          const webResponse = await axios.post('http://localhost:5000/api/web-search', {
            query: searchQuery
          });
          navigate('/chat', { state: { results: webResponse.data, type: 'web' } });
          break;

        case 'app':
          // App builder functionality
          const appResponse = await axios.post('http://localhost:5000/api/app-builder', {
            query: searchQuery
          });
          navigate('/chat', { state: { results: appResponse.data, type: 'app' } });
          break;

        case 'research':
        case 'deep':
          // Deep research functionality
          const researchResponse = await axios.post('http://localhost:5000/api/deep-research', {
            query: searchQuery
          });
          navigate('/chat', { state: { results: researchResponse.data, type: 'research' } });
          break;

        case 'upload':
          // Trigger file upload
          fileInputRef.current?.click();
          break;

        default:
          break;
      }
    } catch (err) {
      setError(err.response?.data?.message || 'An error occurred while processing your request');
      console.error('Search error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files?.[0];
    if (!file) return;

    // Check file size (5MB limit)
    if (file.size > 5 * 1024 * 1024) {
      setError('File size exceeds 5MB limit');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      setLoading(true);
      const uploadResponse = await axios.post('http://localhost:5000/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      navigate('/chat', { state: { results: uploadResponse.data, type: 'upload' } });
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to upload file');
      console.error('Upload error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      handleSearch('deep');
    }
  };

  return (
    <div className={styles.container}>
      <div className={styles.content}>
        {/* Header */}
        <h1 className={styles.title}>
          Ask Fluxa AI <span className={styles.highlight}>Anything</span>
        </h1>

        {/* Navigation */}
        <nav className={styles.nav}>
          <button 
            onClick={() => handleSearch('web')}
            className={styles.navButton}
            disabled={loading}
          >
            <SearchIcon />
            Web Search
          </button>
          <button 
            onClick={() => handleSearch('app')}
            className={styles.navButton}
            disabled={loading}
          >
            <AppsIcon />
            App Builder
          </button>
          <button 
            onClick={() => handleSearch('research')}
            className={styles.navButton}
            disabled={loading}
          >
            <PsychologyIcon />
            Deep Research
          </button>
          <button 
            onClick={() => handleSearch('upload')}
            className={styles.navButton}
            disabled={loading}
          >
            <UploadIcon />
            Upload
          </button>
        </nav>

        {/* Search Input */}
        <div className={styles.searchContainer}>
          <div className={styles.searchInput}>
            <input
              type="text"
              placeholder="Ask anything..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              onKeyPress={handleKeyPress}
              className={styles.input}
              disabled={loading}
            />
            <button className={styles.settingsButton}>
              <SettingsIcon />
            </button>
          </div>
          <button
            onClick={() => handleSearch('deep')}
            className={styles.searchButton}
            disabled={loading}
          >
            {loading ? 'Processing...' : 'Deep Research'}
            <SendIcon />
          </button>
        </div>

        {/* Hidden file input */}
        <input
          type="file"
          ref={fileInputRef}
          onChange={handleFileUpload}
          style={{ display: 'none' }}
          accept="image/*,.pdf,.doc,.docx,.txt"
        />

        {/* Error message */}
        {error && (
          <div className={styles.error}>
            {error}
          </div>
        )}
      </div>
    </div>
  );
};

export default SearchInterface;
