import React from 'react';
import { Box, Typography, List, ListItem, ListItemText, ListItemIcon, IconButton } from '@mui/material';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import StarOutlineIcon from '@mui/icons-material/StarOutline';
import DeleteOutlineIcon from '@mui/icons-material/DeleteOutline';

const History = () => {
  // Mock data - in a real app, this would come from your backend
  const historyItems = [
    { 
      id: 1, 
      query: "How to implement authentication in React?", 
      timestamp: "2 hours ago",
      preview: "To implement authentication in React, you can use solutions like Firebase Auth, Auth0, or implement your own JWT-based system..."
    },
    { 
      id: 2, 
      query: "Explain Docker containers", 
      timestamp: "5 hours ago",
      preview: "Docker containers are lightweight, standalone executable packages that include everything needed to run an application..."
    },
    { 
      id: 3, 
      query: "Best practices for React hooks", 
      timestamp: "1 day ago",
      preview: "Some best practices for React hooks include: following the rules of hooks, using descriptive names, keeping hooks focused..."
    },
  ];

  return (
    <Box sx={{ p: 3, height: '100vh', backgroundColor: '#0F172A' }}>
      <Typography variant="h4" sx={{ color: '#F8FAFC', mb: 3 }}>
        Conversation History
      </Typography>
      <List>
        {historyItems.map((item) => (
          <ListItem
            key={item.id}
            sx={{
              backgroundColor: '#1E293B',
              borderRadius: 1,
              mb: 2,
              '&:hover': {
                backgroundColor: '#334155',
                cursor: 'pointer',
              },
            }}
          >
            <ListItemIcon sx={{ color: '#60A5FA' }}>
              <ChatBubbleOutlineIcon />
            </ListItemIcon>
            <ListItemText
              primary={item.query}
              secondary={
                <Box component="span" sx={{ color: '#94A3B8' }}>
                  <Typography 
                    component="span" 
                    variant="body2" 
                    sx={{ 
                      display: 'block',
                      color: '#94A3B8',
                      mb: 1
                    }}
                  >
                    {item.timestamp}
                  </Typography>
                  <Typography 
                    component="span" 
                    variant="body2" 
                    sx={{ 
                      color: '#CBD5E1',
                      display: '-webkit-box',
                      WebkitLineClamp: 2,
                      WebkitBoxOrient: 'vertical',
                      overflow: 'hidden',
                      textOverflow: 'ellipsis'
                    }}
                  >
                    {item.preview}
                  </Typography>
                </Box>
              }
              sx={{
                '& .MuiListItemText-primary': {
                  color: '#F8FAFC',
                  fontWeight: 500,
                  fontSize: '1.1rem',
                  mb: 1
                }
              }}
            />
            <Box sx={{ display: 'flex', gap: 1 }}>
              <IconButton 
                size="small" 
                sx={{ 
                  color: '#94A3B8',
                  '&:hover': { color: '#FBBF24' }
                }}
              >
                <StarOutlineIcon />
              </IconButton>
              <IconButton 
                size="small" 
                sx={{ 
                  color: '#94A3B8',
                  '&:hover': { color: '#EF4444' }
                }}
              >
                <DeleteOutlineIcon />
              </IconButton>
            </Box>
          </ListItem>
        ))}
      </List>
    </Box>
  );
};

export default History;
